#!/bin/bash
# Development script to run Claude Think Tool plugin

set -e

echo "ðŸš€ Starting Claude Think Tool Plugin Development Mode"
echo ""

# Check if virtual environment exists
if [ ! -d "venv" ]; then
    echo "ðŸ“¦ Creating virtual environment..."
    python3 -m venv venv
fi

# Activate virtual environment
echo "ðŸ”§ Activating virtual environment..."
source venv/bin/activate

# Check Python version
PYTHON_VERSION=$(python --version 2>&1 | awk '{print $2}')
echo "ðŸ Python version: $PYTHON_VERSION"
echo ""

# Install/upgrade dependencies
echo "ðŸ“¥ Installing dependencies..."
pip install -q --upgrade pip
pip install -q -r requirements.txt
echo "âœ… Dependencies installed"
echo ""

# Check if .env exists
if [ ! -f ".env" ]; then
    echo "âš ï¸  .env file not found!"
    echo "ðŸ“ Creating .env from template..."
    cat > .env << 'ENVEOF'
# Dify Plugin Development Configuration
INSTALL_METHOD=remote
REMOTE_INSTALL_URL=localhost:5003
REMOTE_INSTALL_KEY=your-debug-key-here
ENVEOF
    echo ""
    echo "âš ï¸  Please edit .env file and add your Dify debug key:"
    echo "   1. Go to your localhost Dify: http://localhost/plugins"
    echo "   2. Get the Remote Debug Key"
    echo "   3. Update REMOTE_INSTALL_KEY in .env file"
    echo ""
    read -p "Press Enter after updating .env file to continue..."
fi

# Check .env configuration
echo "ðŸ” Checking configuration..."
if grep -q "your-debug-key-here" .env; then
    echo "âš ï¸  Warning: REMOTE_INSTALL_KEY is still set to placeholder!"
    echo "   Please update .env file with your actual debug key"
    echo "   The plugin may not connect without a valid key."
    echo ""
    read -p "Continue anyway? (y/n) " -n 1 -r
    echo ""
    if [[ ! $REPLY =~ ^[Yy]$ ]]; then
        echo "Exiting. Please update .env file first."
        exit 1
    fi
fi

# Display configuration
echo "ðŸ“‹ Current configuration:"
grep -E "INSTALL_METHOD|REMOTE_INSTALL_URL" .env
grep REMOTE_INSTALL_KEY .env | sed 's/REMOTE_INSTALL_KEY=.*/REMOTE_INSTALL_KEY=***hidden***/'
echo ""

# Run the plugin
echo "ðŸŽ¯ Starting plugin..."
REMOTE_URL=$(grep REMOTE_INSTALL_URL .env | cut -d'=' -f2)
echo "   Plugin will connect to: $REMOTE_URL"
echo ""
